
$(function(){if($(".portfolio").length>0){initialize_isotope()}});function initialize_isotope(){var b=$(".isotope").isotope({itemSelector:".portfolio",layoutMode:"fitRows",getSortData:{name:"[data-name]",age:"[data-age]",size:"[data-size]"}});var a={numberGreaterThan50:function(){var c=$(this).find(".number").text();return parseInt(c,10)>50},ium:function(){var c=$(this).find(".name").text();return c.match(/ium$/)}};$("#filters .btn").click(function(){var c=$(this).attr("data-filter");c=a[c]||c;b.isotope({filter:c});$(this).closest(".btn-group").find(".btn").removeClass("btn-success");$(this).addClass("btn-success")});$("#sorts .btn").click(function(){var c=$(this).attr("data-sort-by");b.isotope({sortBy:c})});$("#filters .btn, #sorts .btn").click(function(){$(this).closest(".btn-group").find(".btn").removeClass("btn-success");$(this).closest(".btn-group").find(".btn").addClass("btn-default");$(this).removeClass("btn-default");$(this).addClass("btn-success")})};