
var host=window.location.hostname;var relative_path="/";$(function(){if($(".portfolio").length>0){initialize_isotope()}if(host=="localhost"){relative_path="/rychecky/"}});function portfolio(a){var b=$("#portfolio-modal");$.ajax({type:"GET",url:relative_path+"ajax_portfolio.php",data:{portfolio_id:a},success:function(c,f,e){var d=$.parseJSON(c);b.find(".modal-title").html(d.title);b.find(".modal-body").html(d.body)}});b.modal("show")}function initialize_isotope(){var b=$(".isotope").isotope({itemSelector:".portfolio",layoutMode:"fitRows",getSortData:{name:"[data-name]",age:function(c){return parseInt($(c).data("age"))},size:function(c){return parseInt($(c).data("size"))},}});var a={};$("#filters .btn").click(function(){var c=$(this).attr("data-filter");c=a[c]||c;b.isotope({filter:c});$(this).closest(".btn-group").find(".btn").removeClass("btn-success");$(this).addClass("btn-success")});$("#sorts .btn").click(function(){var c=$(this).attr("data-sort-by");b.isotope({sortBy:c})});$("#filters .btn, #sorts .btn").click(function(){$(this).closest(".btn-group").find(".btn").removeClass("btn-success");$(this).closest(".btn-group").find(".btn").addClass("btn-default");$(this).removeClass("btn-default");$(this).addClass("btn-success")})};